import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import useSound from "use-sound";
import {
	useMoralis,
	useMoralisCloudFunction,
	useMoralisQuery,
} from "react-moralis";
import Confirmation from "../assets/chess_audio/Confirmation.mp3";
import SocialNotify from "../assets/chess_audio/SocialNotify.mp3";
import { ReactComponent as Knight } from "../assets/knight.svg";
import { Card, Image, Tooltip, Modal, Tabs, Skeleton } from "antd";
import {
	FileSearchOutlined,
	SendOutlined,
	GiftOutlined,
	SkinOutlined,
	CheckCircleOutlined,
	WarningOutlined,
} from "@ant-design/icons";
import { getExplorer } from "../helpers/networks";
import AddressInput from "./AddressInput";
import { NFT_TOKEN_ADDRESS } from "../contracts/address";
const { Meta } = Card;
const { TabPane } = Tabs;

const styles = {
	NFTs: {
		display: "flex",
		flexWrap: "wrap",
		WebkitBoxPack: "start",
		justifyContent: "flex-start",
		margin: "2rem auto 0",
		maxWidth: "1000px",
		width: "100%",
		gap: "10px",
	},
};

function NFTBalance() {
	const { Moralis, chainId, user, isWeb3Enabled } = useMoralis();
	const [visible, setVisibility] = useState(false);
	const [receiverToSend, setReceiver] = useState(null);
	const [isPending, setIsPending] = useState(false);
	const [nftToSend, setNftToSend] = useState(null);

	// window.nft = NFTBalances;
	const {
		data: [skinData],
		error: skinError,
		isLoading: isSkinDataLoading,
	} = useMoralisQuery(
		"GameSkin",
		(query) => query.equalTo("userAddress", user?.get("ethAddress")).limit(1),
		[user, isWeb3Enabled],
		{
			autoFetch: true,
			live: true,
		},
	);
	const {
		data: userNFTs,
		error: userNFTError,
		isLoading: isLoadingUserNFTs,
	} = useMoralisQuery(
		"PolygonNFTOwners",
		(query) =>
			query
				.equalTo("owner_of", user?.get("ethAddress"))
				.equalTo("token_address", NFT_TOKEN_ADDRESS),
		[user, isWeb3Enabled],
		{
			autoFetch: true,
			live: true,
		},
	);

	async function transfer(nft, receiver) {
		const options = {
			type: nft.contract_type.toLowerCase(),
			tokenId: nft.token_id,
			receiver: receiver,
			contractAddress: nft.token_address,
			amount: 1,
		};
		// console.log(options);

		setIsPending(true);
		await Moralis.transfer(options)
			.then((tx) => {
				console.log(tx);
				setIsPending(false);
			})
			.catch((e) => {
				alert(e.message);
				setIsPending(false);
			});
	}

	return (
		<>
			<Tabs
				defaultActiveKey="1"
				type="card"
				style={{ marginTop: 40, width: "100%", maxWidth: 1000 }}
			>
				<TabPane key="1" tab="Your NFTs">
					<div style={styles.NFTs}>
						<Skeleton loading={isLoadingUserNFTs}>
							{userNFTs.map(({ attributes }, index) => (
								<NFTCard
									token_id={attributes.token_id}
									token_uri={attributes.token_uri}
									setNftToSend={setNftToSend}
									key={index}
									skinData={skinData}
									setVisibility={setVisibility}
								/>
							))}
							{!userNFTs && (
								<>
									<div
										style={{
											width: "100%",
											display: "flex",
											flexDirection: "column",
											justfyContent: "center",
											alignItems: "center",
										}}
									>
										<Knight style={{ width: 100, height: 100, opacity: 0.5 }} />
										<h1 style={{ fontSize: "2rem", marginTop: "-17.5rem" }}>
											No NFTs found
										</h1>
										<Link
											to="/lobby"
											style={{
												color: "#58c563",
												textDecoration: "none",
												fontSize: "1.2rem",
												cursor: "pointer",
												zIndex: 100,
											}}
										>
											Earn one here
										</Link>
									</div>
								</>
							)}
						</Skeleton>
					</div>
				</TabPane>
				<TabPane key="2" tab="Active Skins">
					{!skinData?.attributes ? (
						<div
							style={{
								display: "flex",
								flexDirection: "column",
								justfyContent: "center",
								alignItems: "center",
							}}
						>
							<Knight style={{ width: 100, opacity: 0.5 }} />
							<h1 style={{ fontSize: "2rem", marginTop: "-17.5rem" }}>
								No Skins in use
							</h1>
						</div>
					) : (
						<div style={styles.NFTs}>
							{Object.keys(skinData?.attributes)
								.filter((key) => key.length === 2)
								.map((pieceKey, index) => (
									<Card
										hoverable
										// loading
										actions={[
											<Tooltip title="View On OpenSea">
												<GiftOutlined
													onClick={() => {
														window
															.open(skinData.get(pieceKey), "_blank")
															.focus();
													}}
												/>
											</Tooltip>,
										]}
										style={{
											width: 300,
											border: "2px solid #e7eaf3",
										}}
										cover={
											<Image
												preview={false}
												src={skinData.get(pieceKey) || "error"}
												fallback="data:image/png;base64,"
												alt=""
												style={{ height: "300px" }}
											/>
										}
										key={index}
									></Card>
								))}
						</div>
					)}
				</TabPane>
			</Tabs>

			<Modal
				title={`Transfer ${nftToSend?.name || "NFT"}`}
				visible={visible}
				onCancel={() => setVisibility(false)}
				onOk={() => transfer(nftToSend, receiverToSend)}
				confirmLoading={isPending}
				okText="Send"
			>
				<AddressInput autoFocus placeholder="Receiver" onChange={setReceiver} />
			</Modal>
		</>
	);
}

const NFTMetaModal = ({
	isNFTMetaModalVisible,
	setIsNFTMetaModalVisible,
	metadata,
	token_uri,
}) => {
	const [playConfirmation] = useSound(Confirmation);

	const mintedAt = new Date(metadata?.minted_at);
	const image = metadata?.image.split("/");
	const piece = metadata?.piece.split("/");
	const {
		fetch: setPieceSkin,
		data: isPieceSkinSet,
		error: pieceSkinError,
		isLoading: settingPieceSkin,
	} = useMoralisCloudFunction(
		"usePieceSkin",
		{
			token_uri: token_uri,
		},
		{
			autoFetch: false,
		},
	);

	const {
		fetch: removePieceSkin,
		data: isPieceSkinRemoved,
		isLoading: removingPieceSkin,
	} = useMoralisCloudFunction(
		"removePieceSkin",
		{
			token_uri: token_uri,
		},
		{
			autoFetch: false,
		},
	);

	function success() {
		Modal.success({
			title: "The Skin is set successfully.",
		});
	}

	function fail() {
		Modal.error({
			title: "Something went wrong",
			content: "Please try again",
		});
	}

	useEffect(() => {
		if (isPieceSkinSet) {
			success();
		}
		if (pieceSkinError) {
			fail();
		}
	}, [isPieceSkinSet, pieceSkinError]);

	const formatAMPM = () => {
		let hours = mintedAt.getHours();
		const ampm = hours >= 12 ? "PM" : "AM";
		hours = hours % 12;
		hours = hours ? hours : 12;
		const strTime = hours + ampm;
		return strTime;
	};

	const { confirm } = Modal;

	function showSetConfirm() {
		confirm({
			title: "Do you Want to set this NFT as Piece Skin?",
			icon: <CheckCircleOutlined />,
			content: "by clicking yes you will set this NFT as the piece skin",
			okText: "Yes",
			okType: "success",
			cancelText: "No",
			onOk: () => {
				setPieceSkin();
			},
			onCancel: () => {
				console.log("Cancel");
				setIsNFTMetaModalVisible(false);
			},
		});
	}
	function showDeleteConfirm() {
		confirm({
			title: "Do you Want to remove this NFT as Piece Skin?",
			icon: <WarningOutlined />,
			content: "by clicking yes you will remove this NFT as the piece skin",
			okText: "Yes",
			okType: "danger",
			cancelText: "No",
			onOk() {
				removePieceSkin();
			},
			onCancel() {
				console.log("Cancel");
			},
		});
	}

	return (
		<>
			<Modal
				visible={isNFTMetaModalVisible}
				okText="Use this NFT as Piece Skin"
				onCancel={() => {
					setIsNFTMetaModalVisible(false);
				}}
				onOk={() => {
					setIsNFTMetaModalVisible(false);
					showSetConfirm();
					playConfirmation();
				}}
				width={450}
				confirmLoading={settingPieceSkin}
				centered={true}
				title={metadata.name}
			>
				<div
					className="images"
					style={{ display: "flex", justifyContent: "space-between" }}
				>
					<div
						style={{
							display: "flex",
							flexDirection: "column",
							alignItems: "center",
							justifyContent: "flex-end",
							marginRight: "0.5rem",
						}}
					>
						<div
							style={{
								fontSize: "0.75rem",
								textAlign: "center",
								marginBottom: "1rem",
							}}
						>
							NFT was minted on
							<div style={{ fontWeight: 800, fontSize: "1rem" }}>
								{mintedAt.toDateString()}
							</div>
							<div style={{ fontWeight: 700, fontSize: "1.5rem" }}>
								{formatAMPM()}
							</div>
						</div>
						<Image
							style={{ width: "8rem" }}
							src={`https://gateway.ipfs.io/ipfs/${piece[2]}/${piece[3]}`}
							alt="PieceNFT"
						/>
					</div>
					<img
						style={{ width: "65%", borderRadius: "0.5rem" }}
						src={`https://gateway.ipfs.io/ipfs/${image[2]}/Shatranj.png`}
						alt="NFT"
					/>
				</div>
				<div
					className="message"
					style={{
						marginTop: "2rem",
						width: "100%",
						textAlign: "center",
						fontSize: "1.1rem",
					}}
				>
					You Defeated{" "}
					<span
						className="white"
						style={{ fontSize: "1.2rem", fontWeight: 700 }}
					>
						{metadata.attributes[2].value.split(" ") ===
						metadata.attributes[0].value
							? metadata.attributes[0].value
							: metadata.attributes[1].value}
						...
					</span>{" "}
					in{" "}
					<span
						className="moves"
						style={{ fontSize: "1.2rem", fontWeight: 700 }}
					>
						{metadata.attributes[3].value}
					</span>{" "}
					moves.
				</div>
			</Modal>
		</>
	);
};

const NFTCard = ({
	token_uri,
	token_id,
	setVisibility,
	skinData,
	setNftToSend,
}) => {
	const [nft, setNft] = useState();
	const { chainId } = useMoralis();
	const [playConfirmation] = useSound(Confirmation);
	const [playSocialNotify] = useSound(SocialNotify);
	const [isNFTMetaModalVisible, setIsNFTMetaModalVisible] = useState(false);
	const [selectedNFT, setSelectedNFT] = useState(null);

	const handleTransferClick = (nft) => {
		setNftToSend(nft);
		setVisibility(true);
		playSocialNotify();
	};
	useEffect(() => {
		fetch(token_uri)
			.then((res) => res.json())
			.then((res) => {
				setNft(res);
			})
			.catch((err) => {
				console.log(err);
			});
	}, [token_uri]);
	return (
		<>
			{isNFTMetaModalVisible && (
				<NFTMetaModal
					isNFTMetaModalVisible={isNFTMetaModalVisible}
					setIsNFTMetaModalVisible={setIsNFTMetaModalVisible}
					nft={selectedNFT}
					sinkData={skinData}
					token_uri={token_uri}
					metadata={nft}
				/>
			)}
			<Card
				hoverable
				// loading
				actions={[
					<Tooltip title="View On Blockexplorer">
						<FileSearchOutlined
							onClick={() => {
								window.open(
									`${getExplorer(chainId)}address/${NFT_TOKEN_ADDRESS}`,
									"_blank",
								);
								// console.log(nft.metadata.image);
							}}
						/>
					</Tooltip>,
					<Tooltip title="Transfer NFT">
						<SendOutlined onClick={() => handleTransferClick(nft)} />
					</Tooltip>,
					<Tooltip title="View On GMI Marketplace">
						<GiftOutlined
							onClick={() =>
								window
									.open(
										`https://app.test.gonnamakeit.com/asset/${NFT_TOKEN_ADDRESS}/${token_id}/`,
										"_blank",
									)
									.focus()
							}
						/>
					</Tooltip>,
					<Tooltip title="Use as Piece Skin">
						<SkinOutlined
							onClick={() => {
								setSelectedNFT(nft);
								setIsNFTMetaModalVisible(true);
								playSocialNotify();
							}}
						/>
					</Tooltip>,
				]}
				style={{
					width: 300,
					border: "2px solid #e7eaf3",
				}}
				cover={
					<Image
						preview={false}
						src={
							`https://gateway.ipfs.io/ipfs/${nft?.image.split("//")[1]}` ||
							"error"
						}
						fallback="data:image/png;base64,"
						alt=""
						style={{ height: "300px" }}
					/>
				}
			>
				<Meta title={nft?.name} description={NFT_TOKEN_ADDRESS} />
			</Card>
		</>
	);
};
export default NFTBalance;
